mapper.insert:
INSERT INTO tb_post (

) 
VALUES (
      #{id}, #{title} , #{content}, #{writer}, 0, #{notice_yn}, 0, NOW(), NULL
)

mapper.update:
UPDATE tb_post
SET modified_date = NOW(), title = #{title}, content = #{content}, notice_yn = #{notice_yn}
WHERE id = #{id}

UPDATE tb_post
SET delete_yn = 1
WHERE id = #{id}
